import './App.css';
import { Component } from 'react';

class App extends Component {

  state = {
    posts: [
      {
        id: 1,
        title: 'O título 1',
        body: 'O corpo 1'
      },
      {
        id: 2,
        title: 'O título 2',
        body: 'O corpo 2'
      },
      {
        id: 3,
        title: 'O título 3',
        body: 'O corpo 3'
      }
    ]
  };

  componentDidMount(){
    this.loadPosts();
  }

  loadPosts = async () => {
    const postResponse = fetch('http://jsonplaceholder.typicode.com/posts');

    const [posts] = await Promise.all([postResponse]);

    const postJson = await posts.json();

    this.setState({posts: postJson})


  }

 render(){
  const {posts} = this.state;
  return (
    <div className="posts">
      {posts.map(post => (
        <div key={post.id} className='post-content'>
          <h1>{post.title}</h1>
          <p>{post.body}</p>
        </div>
      ))}
    </div>
  );
 }
}

export default App;
